const RestaurantState = [];

const restaurantReducer = (state = RestaurantState, action) =>{
  // console.log(action)
  // if(action.type === 'ADD') {
  //   state.push(action.payload);
  //   return [...state]
  // }

  switch(action.type) {
  case RES:
    state.push(action.payload);
    return [...state];

  case 'REMOVE':
    state.splice(action.payload, 1)
    return [...state]

  default:
    return state;

  }

  // return state;
}

export default restaurantReducer;
