const RestaurantState = [];

const restaurantReducer = (state = RestaurantState, action) =>{
  // console.log(action)
  // if(action.type === 'ADD') {
  //   state.push(action.payload);
  //   return [...state]
  // }

  switch(action.type) {
  case 'ADD':
    state.push(action.payload);
    return [...state];

  case 'REMOVE':
    return state.filter(d=>d.index)

    default:
      return state;

  }

  // return state;
}

export default restaurantReducer;
